import React, { useState, useEffect } from 'react';
import { Search, Menu, X, CheckCircle, AlertTriangle, Book, Users, Settings, Home, FileText, Award, ChevronRight, ChevronDown, Play, Bookmark, RotateCcw, User, BarChart3, Check, X as XIcon } from 'lucide-react';

const FlanneryLogo = () => (
  <div className="flex items-center space-x-2">
    <div className="text-xl font-bold text-white tracking-wider">FLANNERY</div>
    <div className="text-xs text-white">®</div>
  </div>
);

const FlanneryTrainingApp = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [searchQuery, setSearchQuery] = useState('');
  const [completedSections, setCompletedSections] = useState(new Set());
  const [knowledgeAnswers, setKnowledgeAnswers] = useState({});
  const [expandedSections, setExpandedSections] = useState(new Set());
  const [showSearch, setShowSearch] = useState(false);
  const [submittedAnswers, setSubmittedAnswers] = useState({});
  const [answerFeedback, setAnswerFeedback] = useState({});
  const [knowledgeProgress, setKnowledgeProgress] = useState({});

  // Knowledge check answers with correct responses
  const correctAnswers = {
    'intro-1': {
      type: 'short-answer',
      correctAnswers: [
        'A 360° excavator is a heavy construction machine that consists of a boom, dipper, bucket and cab on a rotating platform, placed on a wheeled or tracked undercarriage',
        'A 360 excavator is a heavy construction machine with boom, dipper, bucket and cab on rotating platform',
        'Heavy construction machine with rotating platform and tracked undercarriage',
        'Construction machine with boom, dipper, bucket and cab on rotating platform'
      ],
      keywords: ['360', 'excavator', 'boom', 'dipper', 'bucket', 'cab', 'rotating', 'platform', 'tracked', 'undercarriage']
    },
    'intro-2': {
      type: 'multiple-input',
      correctAnswers: [
        'Moving vehicles and machinery',
        'Working at height',
        'Excavations and trenches',
        'Falling materials',
        'Electricity and overhead services',
        'Underground services',
        'Confined spaces',
        'Noise and vibration'
      ],
      requiredCount: 4,
      keywords: ['vehicles', 'machinery', 'height', 'excavations', 'trenches', 'falling', 'materials', 'electricity', 'overhead', 'underground', 'services', 'confined', 'spaces', 'noise', 'vibration']
    },
    'intro-3': {
      type: 'multiple-input',
      correctAnswers: [
        'Injury or death',
        'Property damage',
        'Environmental damage',
        'Financial loss',
        'Legal consequences',
        'Reputation damage',
        'Production delays',
        'Equipment damage'
      ],
      requiredCount: 5,
      keywords: ['injury', 'death', 'damage', 'environmental', 'financial', 'legal', 'reputation', 'delays', 'equipment']
    },
    'legislation-1': {
      type: 'short-answer',
      correctAnswers: [
        'Provide safe working conditions and equipment',
        'Ensure equipment is properly maintained',
        'Provide adequate training and supervision',
        'Conduct risk assessments',
        'Provide safe plant and equipment'
      ],
      keywords: ['safe', 'working', 'conditions', 'equipment', 'maintained', 'training', 'supervision', 'risk', 'assessments']
    },
    'legislation-2': {
      type: 'multiple-input',
      correctAnswers: [
        'Take reasonable care of themselves and others',
        'Co-operate with employer on health and safety',
        'Use equipment and safety devices properly',
        'Follow safety procedures',
        'Report hazards and incidents'
      ],
      requiredCount: 3,
      keywords: ['reasonable', 'care', 'themselves', 'others', 'co-operate', 'employer', 'health', 'safety', 'equipment', 'safety', 'devices', 'procedures', 'report', 'hazards', 'incidents']
    },
    'components-1': {
      type: 'short-answer',
      correctAnswers: [
        'To prevent skin disease and contamination',
        'Prevent skin disease',
        'Avoid contamination of oil onto operating controls',
        'Prevent skin disease and oil contamination',
        'To protect against skin disease'
      ],
      keywords: ['skin', 'disease', 'contamination', 'oil', 'controls', 'protect']
    },
    'components-2': {
      type: 'multiple-input',
      correctAnswers: [
        'Ensure filler cap area is clean',
        'Release pressure in system before removing cap',
        'Use clean container when filling',
        'Clean the filler cap area',
        'Depressurize system before removing cap'
      ],
      requiredCount: 2,
      keywords: ['clean', 'filler', 'cap', 'area', 'pressure', 'released', 'clean', 'container', 'filling', 'depressurize']
    },
    'components-3': {
      type: 'short-answer',
      correctAnswers: [
        'To prevent condensation building up in tank',
        'Prevent condensation in fuel tank',
        'Avoid condensation in tank',
        'Prevent moisture build up in tank'
      ],
      keywords: ['condensation', 'tank', 'moisture', 'build', 'prevent']
