  // Validation function
  const validateAnswer = (questionId, userAnswer) => {
    const correctAnswer = correctAnswers[questionId];
    if (!correctAnswer) return { isCorrect: false, feedback: 'Question not found' };

    const userAnswerLower = userAnswer.toLowerCase().trim();

    if (correctAnswer.type === 'short-answer') {
      // Check if user answer matches any correct answer
      const isCorrect = correctAnswer.correctAnswers.some(correct => 
        correct.toLowerCase().includes(userAnswerLower) || 
        userAnswerLower.includes(correct.toLowerCase())
      );

      // Check for keyword matching as fallback
      const keywordMatch = correctAnswer.keywords.some(keyword => 
        userAnswerLower.includes(keyword.toLowerCase())
      );

      return {
        isCorrect: isCorrect || keywordMatch,
        feedback: isCorrect || keywordMatch ? 'Correct!' : 'Incorrect. Please review the material and try again.',
        correctAnswer: correctAnswer.correctAnswers[0]
      };
    } else if (correctAnswer.type === 'multiple-input') {
      const userAnswers = userAnswer.split(',').map(ans => ans.trim().toLowerCase());
      const correctAnswersLower = correctAnswer.correctAnswers.map(ans => ans.toLowerCase());

      const correctMatches = userAnswers.filter(userAns => 
        correctAnswersLower.some(correctAns => 
          correctAns.includes(userAns) || userAns.includes(correctAns)
        )
      );

      const isCorrect = correctMatches.length >= correctAnswer.requiredCount;

      return {
        isCorrect,
        feedback: isCorrect ? 'Correct!' : `Incorrect. You need at least ${correctAnswer.requiredCount} correct answers.`,
        correctAnswer: correctAnswer.correctAnswers.slice(0, correctAnswer.requiredCount).join(', ')
      };
    }

    return { isCorrect: false, feedback: 'Invalid question type' };
  };

  const handleSubmitAnswer = (questionId) => {
    const userAnswer = knowledgeAnswers[questionId] || '';
    const validation = validateAnswer(questionId, userAnswer);

    setSubmittedAnswers(prev => ({ ...prev, [questionId]: true }));
    setAnswerFeedback(prev => ({ ...prev, [questionId]: validation }));

    // Update knowledge progress
    if (validation.isCorrect) {
      setKnowledgeProgress(prev => ({ ...prev, [questionId]: 'correct' }));
    } else {
      setKnowledgeProgress(prev => ({ ...prev, [questionId]: 'incorrect' }));
    }
  };

  const handleResetAnswer = (questionId) => {
    setKnowledgeAnswers(prev => ({ ...prev, [questionId]: '' }));
    setSubmittedAnswers(prev => ({ ...prev, [questionId]: false }));
    setAnswerFeedback(prev => ({ ...prev, [questionId]: null }));
  };

