  useEffect(() => {
    const interval = setInterval(() => {
      if (isActivelyLearning(activeSection)) {
        setCurrentSessionTime(Date.now() - sessionStartTime);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [sessionStartTime, activeSection]);

