import React, { useState, useEffect } from 'react';
import { Search, Menu, X, CheckCircle, AlertTriangle, Book, Users, Settings, Home, FileText, Award, ChevronRight, ChevronDown, Play, Bookmark, RotateCcw, User, BarChart3 } from 'lucide-react';

const FlanneryTrainingApp = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [searchQuery, setSearchQuery] = useState('');
  const [completedSections, setCompletedSections] = useState(new Set());
  const [knowledgeAnswers, setKnowledgeAnswers] = useState({});
  const [expandedSections, setExpandedSections] = useState(new Set(['introduction']));
  const [showSearch, setShowSearch] = useState(false);

  const trainingData = {
    dashboard: {
      title: 'Training Dashboard',
      icon: Home,
      content: 'dashboard'
    },
    introduction: {
      title: 'Introduction to 360 Excavator',
      icon: Book,
      content: {
        overview: 'A 360Â° excavator is a heavy construction machine that consists of a boom, dipper, bucket and cab on a rotating platform, placed on a wheeled or tracked undercarriage.',
        objectives: [
          'Understand relevant legislation relating to work activities',
          'Comply with manufacturer\'s instructions using operator\'s handbook',
          'Identify hazards and implement appropriate control measures',
          'Identify machine components and operator controls',
          'Perform pre-shift and operational checks',
          'Prepare excavator for site travel and maneuver safely',
          'Demonstrate safe attachment/removal of buckets and ancillaries',
          'Operate plant to excavate ground and loose materials',
          'Perform excavation activities in various soil types',
          'Load transporting vehicles safely',
          'Grade, spread and level different materials',
          'Load/unload on transporters safely',
          'Shut down machinery safely'
        ]
      }
    },
    safety: {
      title: 'Health & Safety Legislation',
      icon: AlertTriangle,
      subsections: {
        'hse-act': {
          title: 'Health and Safety at Work Act 1974',
          content: {
            purpose: 'Designed to protect people and the environment from workplace activities',
            responsibilities: [
              'Employers must provide safe working conditions',
              'Employees must take reasonable care of themselves and others',
              'Equipment must be properly maintained and inspected'
            ]
          }
        },
        'puwer': {
          title: 'PUWER 98 Regulations',
          content: {
            purpose: 'Provision & Use of Work Equipment Regulations 1998',
            requirements: [
              'Equipment must be suitable for intended use',
              'Equipment must be maintained in safe condition',
              'Users must receive adequate information and training',
              'Equipment must have appropriate safety measures'
            ]
          }
        }
      }
    },
    components: {
      title: 'Major Components',
      icon: Settings,
      content: {
        powerUnit: {
          title: 'Power Unit & Oils',
          safety: 'Always wear gloves when checking engine oil to prevent skin disease'
        },
        hydraulicSystem: {
          title: 'Hydraulic System',
          safety: 'Ensure filler cap area is clean and pressure is released before removing cap'
        },
        safetyFeatures: {
          title: 'Safety Features',
          rops: 'Roll Over Protective Structure - provides protection in event of overturn',
          fops: 'Falling Object Protective Structure - protects from falling materials',
          seatbelt: 'Must be worn even with cab door closed'
        }
      }
    },
    operations: {
      title: 'Operating Procedures',
      icon: Play,
      subsections: {
        'pre-checks': {
          title: 'Pre-Operational Checks',
          content: {
            items: [
              'Hydraulic oil system level',
              'Windscreen washer level',
              'Track adjustment',
              'Mirrors and windows',
              'Seat belt inspection',
              'Indicators and gauges'
            ],
            running: [
              'Hydraulic lockout control',
              'Lights and flashing beacon',
              'Horn functionality',
              'Slew and movement',
              'Quick hitch operation'
            ]
          }
        },
        'site-travel': {
          title: 'Site Travel Configuration',
          content: {
            checklist: [
              'Check controls are neutralized',
              'Adjust seat for comfort/reach',
              'Wear and adjust seatbelt',
              'Turn engine speed to operating range',
              'Move hydraulic lockout to unlocked position',
              'Raise boom for ground clearance',
              'Drive sprockets to rear of machine'
            ]
          }
        }
      }
    },
    excavation: {
      title: 'Excavation Techniques',
      icon: FileText,
      content: {
        types: [
          { name: 'Topsoil excavation', description: 'Removal of exposed layer' },
          { name: 'Earth excavation', description: 'Removal of layer beneath topsoil' },
          { name: 'Rock excavation', description: 'Removal requiring special methods' },
          { name: 'Trench excavation', description: 'Length greatly exceeds depth' }
        ],
        techniques: [
          'Always start at top and work in layers',
          'Begin at edge of trench',
          'Face excavator toward work area',
          'Work within machine\'s digging envelope'
        ]
      }
    }
  };

  const knowledgeChecks = [
    {
      id: 'safety-1',
      question: 'What is a 360 Excavator?',
      section: 'introduction',
      type: 'short-answer'
    },
    {
      id: 'safety-2',
      question: 'List 4 main hazards commonly found on construction sites:',
      section: 'safety',
      type: 'multiple-input'
    },
    {
      id: 'components-1',
      question: 'Why must gloves always be worn when checking engine oil?',
      section: 'components',
      type: 'short-answer'
    }
  ];

  const navigationItems = [
    { key: 'dashboard', label: 'Dashboard', icon: Home },
    { key: 'training', label: 'Training', icon: Book },
    { key: 'progress', label: 'Progress', icon: BarChart3 },
    { key: 'profile', label: 'Profile', icon: User }
  ];

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => {
      const newExpanded = new Set(prev);
      if (newExpanded.has(sectionId)) {
        newExpanded.delete(sectionId);
      } else {
        newExpanded.add(sectionId);
      }
      return newExpanded;
    });
  };

  const markSectionComplete = (sectionId) => {
    setCompletedSections(prev => new Set([...prev, sectionId]));
  };

  const DashboardContent = () => (
    <div className="space-y-4">
      <div className="bg-gradient-to-r from-orange-500 to-orange-600 text-white p-6 rounded-lg">
        <h1 className="text-2xl font-bold mb-2">Skills Bootcamp</h1>
        <p className="text-lg">360 Excavator Training</p>
      </div>

      <div className="grid grid-cols-1 gap-4">
        <div className="bg-white p-4 rounded-lg shadow border">
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-lg font-semibold">Progress</h3>
            <CheckCircle className="h-5 w-5 text-green-500" />
          </div>
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Completed</span>
              <span className="font-semibold">{completedSections.size}/6</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-orange-500 h-2 rounded-full transition-all"
                style={{ width: `${(completedSections.size / 6) * 100}%` }}
              ></div>
            </div>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow border">
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-lg font-semibold">Safety Focus</h3>
            <AlertTriangle className="h-5 w-5 text-amber-500" />
          </div>
          <p className="text-gray-600 text-sm">Always remember to OperateSAFE - your safety and that of others is paramount.</p>
        </div>

        <div className="bg-white p-4 rounded-lg shadow border">
          <h3 className="text-lg font-semibold mb-3">Quick Actions</h3>
          <div className="space-y-2">
            <button className="w-full text-left text-blue-600 hover:text-blue-800 text-sm py-2">Take Knowledge Check</button>
            <button className="w-full text-left text-blue-600 hover:text-blue-800 text-sm py-2">Review Safety Guidelines</button>
            <button className="w-full text-left text-blue-600 hover:text-blue-800 text-sm py-2">Bookmark Section</button>
          </div>
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow border">
        <h3 className="text-lg font-semibold mb-3">Training Objectives</h3>
        <div className="space-y-2">
          {trainingData.introduction.content.objectives.slice(0, 5).map((objective, index) => (
            <div key={index} className="flex items-start space-x-2">
              <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
              <span className="text-gray-700 text-sm">{objective}</span>
            </div>
          ))}
          <button className="text-orange-600 text-sm font-medium mt-2">View All Objectives â</button>
        </div>
      </div>
    </div>
  );

  const TrainingContent = () => (
    <div className="space-y-4">
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-xl font-bold text-gray-900">Training Modules</h1>
        <button 
          onClick={() => setShowSearch(!showSearch)}
          className="p-2 hover:bg-gray-100 rounded-lg"
        >
          <Search className="h-5 w-5" />
        </button>
      </div>

      {showSearch && (
        <div className="mb-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search training content..."
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>
      )}

      <div className="space-y-3">
        {Object.entries(trainingData).filter(([key]) => key !== 'dashboard').map(([key, section]) => {
          const Icon = section.icon;
          const isCompleted = completedSections.has(key);

          return (
            <div key={key} className="bg-white rounded-lg shadow border overflow-hidden">
              <button
                onClick={() => setActiveSection(key)}
                className="w-full p-4 text-left flex items-center justify-between hover:bg-gray-50"
              >
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-orange-100 rounded-lg flex items-center justify-center">
                    <Icon className="h-5 w-5 text-orange-600" />
                  </div>
                  <div>
                    <h3 className="font-semibold text-gray-900">{section.title}</h3>
                    <p className="text-sm text-gray-500">Training module</p>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  {isCompleted && <CheckCircle className="h-4 w-4 text-green-500" />}
                  <ChevronRight className="h-4 w-4 text-gray-400" />
                </div>
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );

  const ProgressContent = () => (
    <div className="space-y-4">
      <h1 className="text-xl font-bold text-gray-900 mb-4">Your Progress</h1>

      <div className="bg-white p-4 rounded-lg shadow border">
        <h3 className="text-lg font-semibold mb-3">Overall Progress</h3>
        <div className="space-y-3">
          <div className="flex justify-between items-center">
            <span className="text-sm text-gray-600">Completed Sections</span>
            <span className="font-semibold">{completedSections.size}/6</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-3">
            <div 
              className="bg-orange-500 h-3 rounded-full transition-all"
              style={{ width: `${(completedSections.size / 6) * 100}%` }}
            ></div>
          </div>
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow border">
        <h3 className="text-lg font-semibold mb-3">Recent Activity</h3>
        <div className="space-y-3">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center">
              <CheckCircle className="h-4 w-4 text-green-600" />
            </div>
            <div className="flex-1">
              <p className="text-sm font-medium">Completed Introduction</p>
              <p className="text-xs text-gray-500">2 hours ago</p>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
              <Book className="h-4 w-4 text-blue-600" />
            </div>
            <div className="flex-1">
              <p className="text-sm font-medium">Started Safety Module</p>
              <p className="text-xs text-gray-500">1 day ago</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const ProfileContent = () => (
    <div className="space-y-4">
      <h1 className="text-xl font-bold text-gray-900 mb-4">Profile</h1>

      <div className="bg-white p-4 rounded-lg shadow border">
        <div className="flex items-center space-x-3 mb-4">
          <div className="w-12 h-12 bg-orange-100 rounded-full flex items-center justify-center">
            <User className="h-6 w-6 text-orange-600" />
          </div>
          <div>
            <h3 className="font-semibold">John Smith</h3>
            <p className="text-sm text-gray-500">Excavator Operator</p>
          </div>
        </div>

        <div className="space-y-3">
          <div className="flex justify-between">
            <span className="text-sm text-gray-600">Experience</span>
            <span className="text-sm font-medium">5 years</span>
          </div>
          <div className="flex justify-between">
            <span className="text-sm text-gray-600">Training Hours</span>
            <span className="text-sm font-medium">48.5 hours</span>
          </div>
          <div className="flex justify-between">
            <span className="text-sm text-gray-600">Certifications</span>
            <span className="text-sm font-medium">3</span>
          </div>
        </div>
      </div>

      <div className="bg-white p-4 rounded-lg shadow border">
        <h3 className="text-lg font-semibold mb-3">Settings</h3>
        <div className="space-y-3">
          <button className="w-full text-left py-2 hover:bg-gray-50 rounded">
            <div className="flex items-center justify-between">
              <span className="text-sm">Notifications</span>
              <ChevronRight className="h-4 w-4 text-gray-400" />
            </div>
          </button>
          <button className="w-full text-left py-2 hover:bg-gray-50 rounded">
            <div className="flex items-center justify-between">
              <span className="text-sm">Privacy</span>
              <ChevronRight className="h-4 w-4 text-gray-400" />
            </div>
          </button>
          <button className="w-full text-left py-2 hover:bg-gray-50 rounded">
            <div className="flex items-center justify-between">
              <span className="text-sm">Help & Support</span>
              <ChevronRight className="h-4 w-4 text-gray-400" />
            </div>
          </button>
        </div>
      </div>
    </div>
  );

  const KnowledgeCheck = ({ check }) => (
    <div className="bg-blue-50 border border-blue-200 p-4 rounded-lg mb-4">
      <h4 className="font-semibold text-blue-900 mb-2">KNOWLEDGE STOP</h4>
      <p className="text-blue-800 mb-3 text-sm">{check.question}</p>
      <textarea 
        className="w-full p-3 border border-blue-300 rounded-md resize-none text-sm"
        rows="3"
        placeholder="Enter your answer here..."
        value={knowledgeAnswers[check.id] || ''}
        onChange={(e) => setKnowledgeAnswers(prev => ({
          ...prev,
          [check.id]: e.target.value
        }))}
      />
      <button className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 text-sm">
        Submit Answer
      </button>
    </div>
  );

  const SectionContent = ({ sectionKey, data }) => {
    if (sectionKey === 'dashboard') {
      return <DashboardContent />;
    }

    const relevantChecks = knowledgeChecks.filter(check => check.section === sectionKey);

    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-bold text-gray-900">{data.title}</h1>
          <button
            onClick={() => markSectionComplete(sectionKey)}
            className={`px-4 py-2 rounded-lg flex items-center space-x-2 text-sm ${
              completedSections.has(sectionKey) 
                ? 'bg-green-100 text-green-700' 
                : 'bg-orange-500 text-white hover:bg-orange-600'
            }`}
          >
            <CheckCircle className="h-4 w-4" />
            <span>{completedSections.has(sectionKey) ? 'Completed' : 'Complete'}</span>
          </button>
        </div>

        {data.content && typeof data.content === 'object' && (
          <div className="space-y-4">
            {Object.entries(data.content).map(([key, value]) => (
              <div key={key} className="bg-white p-4 rounded-lg shadow border">
                {typeof value === 'string' ? (
                  <p className="text-gray-700 text-sm">{value}</p>
                ) : Array.isArray(value) ? (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 capitalize">{key.replace(/([A-Z])/g, ' $1')}</h3>
                    <ul className="space-y-2">
                      {value.map((item, index) => (
                        <li key={index} className="flex items-start space-x-2">
                          <ChevronRight className="h-4 w-4 text-orange-500 mt-0.5 flex-shrink-0" />
                          <span className="text-gray-700 text-sm">
                            {typeof item === 'object' ? (
                              <div>
                                <strong>{item.name}:</strong> {item.description}
                              </div>
                            ) : (
                              item
                            )}
                          </span>
                        </li>
                      ))}
                    </ul>
                  </div>
                ) : (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 capitalize">{key.replace(/([A-Z])/g, ' $1')}</h3>
                    <div className="space-y-2">
                      {Object.entries(value).map(([subKey, subValue]) => (
                        <div key={subKey} className="border-l-4 border-orange-500 pl-3">
                          <h4 className="font-medium capitalize text-sm">{subKey.replace(/([A-Z])/g, ' $1')}:</h4>
                          <p className="text-gray-700 mt-1 text-sm">{subValue}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {data.subsections && (
          <div className="space-y-3">
            {Object.entries(data.subsections).map(([subKey, subData]) => (
              <div key={subKey} className="bg-white rounded-lg shadow border">
                <button
                  onClick={() => toggleSection(subKey)}
                  className="w-full p-4 text-left flex items-center justify-between hover:bg-gray-50"
                >
                  <h3 className="text-lg font-semibold">{subData.title}</h3>
                  {expandedSections.has(subKey) ? 
                    <ChevronDown className="h-4 w-4" /> : 
                    <ChevronRight className="h-4 w-4" />
                  }
                </button>
                {expandedSections.has(subKey) && (
                  <div className="px-4 pb-4 border-t border-gray-100">
                    <div className="pt-3 space-y-3">
                      {subData.content.purpose && (
                        <div>
                          <h4 className="font-medium mb-2 text-sm">Purpose:</h4>
                          <p className="text-gray-700 text-sm">{subData.content.purpose}</p>
                        </div>
                      )}
                      {subData.content.requirements && (
                        <div>
                          <h4 className="font-medium mb-2 text-sm">Requirements:</h4>
                          <ul className="space-y-1">
                            {subData.content.requirements.map((req, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <span className="text-orange-500 mt-1 text-sm">â¢</span>
                                <span className="text-gray-700 text-sm">{req}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                      {subData.content.responsibilities && (
                        <div>
                          <h4 className="font-medium mb-2 text-sm">Key Responsibilities:</h4>
                          <ul className="space-y-1">
                            {subData.content.responsibilities.map((resp, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <span className="text-orange-500 mt-1 text-sm">â¢</span>
                                <span className="text-gray-700 text-sm">{resp}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {relevantChecks.map((check, index) => (
          <KnowledgeCheck key={check.id} check={check} />
        ))}
      </div>
    );
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'dashboard':
        return <DashboardContent />;
      case 'training':
        return <TrainingContent />;
      case 'progress':
        return <ProgressContent />;
      case 'profile':
        return <ProfileContent />;
      default:
        return <SectionContent sectionKey={activeSection} data={trainingData[activeSection]} />;
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 p-4">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-lg font-semibold text-gray-900">
              {activeSection === 'dashboard' ? 'Flannery Training' : 
               activeSection === 'training' ? 'Training Modules' :
               activeSection === 'progress' ? 'Progress' :
               activeSection === 'profile' ? 'Profile' :
               trainingData[activeSection]?.title || 'Training'}
            </h1>
            <p className="text-xs text-gray-500">Mobile Training Platform</p>
          </div>
          <div className="flex items-center space-x-2">
            <button className="p-2 hover:bg-gray-100 rounded-lg" title="Bookmark">
              <Bookmark className="h-5 w-5 text-gray-600" />
            </button>
            <button className="p-2 hover:bg-gray-100 rounded-lg" title="Reset">
              <RotateCcw className="h-5 w-5 text-gray-600" />
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-auto p-4">
        {renderContent()}
      </main>

      {/* Bottom Navigation */}
      <nav className="bg-white border-t border-gray-200 px-4 py-2">
        <div className="flex justify-around">
          {navigationItems.map((item) => {
            const Icon = item.icon;
            const isActive = activeSection === item.key;

            return (
              <button
                key={item.key}
                onClick={() => setActiveSection(item.key)}
                className={`flex flex-col items-center py-2 px-3 rounded-lg transition-colors ${
                  isActive 
                    ? 'text-orange-600 bg-orange-50' 
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <Icon className="h-5 w-5 mb-1" />
                <span className="text-xs font-medium">{item.label}</span>
              </button>
            );
          })}
        </div>
      </nav>
    </div>
  );
};

export default FlanneryTrainingApp;
