import React, { useState, useEffect } from 'react';
import { Search, Menu, X, CheckCircle, AlertTriangle, Book, Users, Settings, Home, FileText, Award, ChevronRight, ChevronDown, Play, Bookmark, RotateCcw } from 'lucide-react';

const FlanneryTrainingApp = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [completedSections, setCompletedSections] = useState(new Set());
  const [knowledgeAnswers, setKnowledgeAnswers] = useState({});
  const [expandedSections, setExpandedSections] = useState(new Set(['introduction']));

  const trainingData = {
    dashboard: {
      title: 'Training Dashboard',
      icon: Home,
      content: 'dashboard'
    },
    introduction: {
      title: 'Introduction to 360 Excavator',
      icon: Book,
      content: {
        overview: 'A 360Â° excavator is a heavy construction machine that consists of a boom, dipper, bucket and cab on a rotating platform, placed on a wheeled or tracked undercarriage.',
        objectives: [
          'Understand relevant legislation relating to work activities',
          'Comply with manufacturer\'s instructions using operator\'s handbook',
          'Identify hazards and implement appropriate control measures',
          'Identify machine components and operator controls',
          'Perform pre-shift and operational checks',
          'Prepare excavator for site travel and maneuver safely',
          'Demonstrate safe attachment/removal of buckets and ancillaries',
          'Operate plant to excavate ground and loose materials',
          'Perform excavation activities in various soil types',
          'Load transporting vehicles safely',
          'Grade, spread and level different materials',
          'Load/unload on transporters safely',
          'Shut down machinery safely'
        ]
      }
    },
    safety: {
      title: 'Health & Safety Legislation',
      icon: AlertTriangle,
      subsections: {
        'hse-act': {
          title: 'Health and Safety at Work Act 1974',
          content: {
            purpose: 'Designed to protect people and the environment from workplace activities',
            responsibilities: [
              'Employers must provide safe working conditions',
              'Employees must take reasonable care of themselves and others',
              'Equipment must be properly maintained and inspected'
            ]
          }
        },
        'puwer': {
          title: 'PUWER 98 Regulations',
          content: {
            purpose: 'Provision & Use of Work Equipment Regulations 1998',
            requirements: [
              'Equipment must be suitable for intended use',
              'Equipment must be maintained in safe condition',
              'Users must receive adequate information and training',
              'Equipment must have appropriate safety measures'
            ]
          }
        }
      }
    },
    components: {
      title: 'Major Components',
      icon: Settings,
      content: {
        powerUnit: {
          title: 'Power Unit & Oils',
          safety: 'Always wear gloves when checking engine oil to prevent skin disease'
        },
        hydraulicSystem: {
          title: 'Hydraulic System',
          safety: 'Ensure filler cap area is clean and pressure is released before removing cap'
        },
        safetyFeatures: {
          title: 'Safety Features',
          rops: 'Roll Over Protective Structure - provides protection in event of overturn',
          fops: 'Falling Object Protective Structure - protects from falling materials',
          seatbelt: 'Must be worn even with cab door closed'
        }
      }
    },
    operations: {
      title: 'Operating Procedures',
      icon: Play,
      subsections: {
        'pre-checks': {
          title: 'Pre-Operational Checks',
          content: {
            items: [
              'Hydraulic oil system level',
              'Windscreen washer level',
              'Track adjustment',
              'Mirrors and windows',
              'Seat belt inspection',
              'Indicators and gauges'
            ],
            running: [
              'Hydraulic lockout control',
              'Lights and flashing beacon',
              'Horn functionality',
              'Slew and movement',
              'Quick hitch operation'
            ]
          }
        },
        'site-travel': {
          title: 'Site Travel Configuration',
          content: {
            checklist: [
              'Check controls are neutralized',
              'Adjust seat for comfort/reach',
              'Wear and adjust seatbelt',
              'Turn engine speed to operating range',
              'Move hydraulic lockout to unlocked position',
              'Raise boom for ground clearance',
              'Drive sprockets to rear of machine'
            ]
          }
        }
      }
    },
    excavation: {
      title: 'Excavation Techniques',
      icon: FileText,
      content: {
        types: [
          { name: 'Topsoil excavation', description: 'Removal of exposed layer' },
          { name: 'Earth excavation', description: 'Removal of layer beneath topsoil' },
          { name: 'Rock excavation', description: 'Removal requiring special methods' },
          { name: 'Trench excavation', description: 'Length greatly exceeds depth' }
        ],
        techniques: [
          'Always start at top and work in layers',
          'Begin at edge of trench',
          'Face excavator toward work area',
          'Work within machine\'s digging envelope'
        ]
      }
    }
  };

  const knowledgeChecks = [
    {
      id: 'safety-1',
      question: 'What is a 360 Excavator?',
      section: 'introduction',
      type: 'short-answer'
    },
    {
      id: 'safety-2',
      question: 'List 4 main hazards commonly found on construction sites:',
      section: 'safety',
      type: 'multiple-input'
    },
    {
      id: 'components-1',
      question: 'Why must gloves always be worn when checking engine oil?',
      section: 'components',
      type: 'short-answer'
    }
  ];

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => {
      const newExpanded = new Set(prev);
      if (newExpanded.has(sectionId)) {
        newExpanded.delete(sectionId);
      } else {
        newExpanded.add(sectionId);
      }
      return newExpanded;
    });
  };

  const markSectionComplete = (sectionId) => {
    setCompletedSections(prev => new Set([...prev, sectionId]));
  };

  const DashboardContent = () => (
    <div className="space-y-6">
      <div className="bg-gradient-to-r from-orange-500 to-orange-600 text-white p-6 rounded-lg">
        <h1 className="text-3xl font-bold mb-2">Skills Bootcamp in Plant Operations</h1>
        <p className="text-xl">360 Excavator Training Programme</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-6 rounded-lg shadow border">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">Progress Overview</h3>
            <CheckCircle className="h-6 w-6 text-green-500" />
          </div>
          <div className="space-y-2">
            <div className="flex justify-between">
              <span>Completed Sections</span>
              <span className="font-semibold">{completedSections.size}/6</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-orange-500 h-2 rounded-full transition-all"
                style={{ width: `${(completedSections.size / 6) * 100}%` }}
              ></div>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow border">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">Safety Focus</h3>
            <AlertTriangle className="h-6 w-6 text-amber-500" />
          </div>
          <p className="text-gray-600">Always remember to OperateSAFE - your safety and that of others is paramount in all plant operations.</p>
        </div>

        <div className="bg-white p-6 rounded-lg shadow border">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">Quick Actions</h3>
            <Award className="h-6 w-6 text-blue-500" />
          </div>
          <div className="space-y-2">
            <button className="w-full text-left text-blue-600 hover:text-blue-800">Take Knowledge Check</button>
            <button className="w-full text-left text-blue-600 hover:text-blue-800">Review Safety Guidelines</button>
            <button className="w-full text-left text-blue-600 hover:text-blue-800">Bookmark Section</button>
          </div>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow border">
        <h3 className="text-xl font-semibold mb-4">Training Objectives</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {trainingData.introduction.content.objectives.map((objective, index) => (
            <div key={index} className="flex items-start space-x-3">
              <CheckCircle className="h-5 w-5 text-green-500 mt-0.5 flex-shrink-0" />
              <span className="text-gray-700">{objective}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const KnowledgeCheck = ({ check }) => (
    <div className="bg-blue-50 border border-blue-200 p-4 rounded-lg mb-6">
      <h4 className="font-semibold text-blue-900 mb-2">KNOWLEDGE STOP</h4>
      <p className="text-blue-800 mb-3">{check.question}</p>
      <textarea 
        className="w-full p-3 border border-blue-300 rounded-md resize-none"
        rows="3"
        placeholder="Enter your answer here..."
        value={knowledgeAnswers[check.id] || ''}
        onChange={(e) => setKnowledgeAnswers(prev => ({
          ...prev,
          [check.id]: e.target.value
        }))}
      />
      <button className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
        Submit Answer
      </button>
    </div>
  );

  const SectionContent = ({ sectionKey, data }) => {
    if (sectionKey === 'dashboard') {
      return <DashboardContent />;
    }

    const relevantChecks = knowledgeChecks.filter(check => check.section === sectionKey);

    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold text-gray-900">{data.title}</h1>
          <button
            onClick={() => markSectionComplete(sectionKey)}
            className={`px-4 py-2 rounded-lg flex items-center space-x-2 ${
              completedSections.has(sectionKey) 
                ? 'bg-green-100 text-green-700' 
                : 'bg-orange-500 text-white hover:bg-orange-600'
            }`}
          >
            <CheckCircle className="h-5 w-5" />
            <span>{completedSections.has(sectionKey) ? 'Completed' : 'Mark Complete'}</span>
          </button>
        </div>

        {data.content && typeof data.content === 'object' && (
          <div className="space-y-6">
            {Object.entries(data.content).map(([key, value]) => (
              <div key={key} className="bg-white p-6 rounded-lg shadow border">
                {typeof value === 'string' ? (
                  <p className="text-gray-700">{value}</p>
                ) : Array.isArray(value) ? (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 capitalize">{key.replace(/([A-Z])/g, ' $1')}</h3>
                    <ul className="space-y-2">
                      {value.map((item, index) => (
                        <li key={index} className="flex items-start space-x-3">
                          <ChevronRight className="h-5 w-5 text-orange-500 mt-0.5 flex-shrink-0" />
                          <span className="text-gray-700">
                            {typeof item === 'object' ? (
                              <div>
                                <strong>{item.name}:</strong> {item.description}
                              </div>
                            ) : (
                              item
                            )}
                          </span>
                        </li>
                      ))}
                    </ul>
                  </div>
                ) : (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 capitalize">{key.replace(/([A-Z])/g, ' $1')}</h3>
                    <div className="space-y-2">
                      {Object.entries(value).map(([subKey, subValue]) => (
                        <div key={subKey} className="border-l-4 border-orange-500 pl-4">
                          <h4 className="font-medium capitalize">{subKey.replace(/([A-Z])/g, ' $1')}:</h4>
                          <p className="text-gray-700 mt-1">{subValue}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {data.subsections && (
          <div className="space-y-4">
            {Object.entries(data.subsections).map(([subKey, subData]) => (
              <div key={subKey} className="bg-white rounded-lg shadow border">
                <button
                  onClick={() => toggleSection(subKey)}
                  className="w-full p-6 text-left flex items-center justify-between hover:bg-gray-50"
                >
                  <h3 className="text-lg font-semibold">{subData.title}</h3>
                  {expandedSections.has(subKey) ? 
                    <ChevronDown className="h-5 w-5" /> : 
                    <ChevronRight className="h-5 w-5" />
                  }
                </button>
                {expandedSections.has(subKey) && (
                  <div className="px-6 pb-6 border-t border-gray-100">
                    <div className="pt-4 space-y-4">
                      {subData.content.purpose && (
                        <div>
                          <h4 className="font-medium mb-2">Purpose:</h4>
                          <p className="text-gray-700">{subData.content.purpose}</p>
                        </div>
                      )}
                      {subData.content.requirements && (
                        <div>
                          <h4 className="font-medium mb-2">Requirements:</h4>
                          <ul className="space-y-1">
                            {subData.content.requirements.map((req, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <span className="text-orange-500 mt-1">â¢</span>
                                <span className="text-gray-700">{req}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                      {subData.content.responsibilities && (
                        <div>
                          <h4 className="font-medium mb-2">Key Responsibilities:</h4>
                          <ul className="space-y-1">
                            {subData.content.responsibilities.map((resp, index) => (
                              <li key={index} className="flex items-start space-x-2">
                                <span className="text-orange-500 mt-1">â¢</span>
                                <span className="text-gray-700">{resp}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {relevantChecks.map((check, index) => (
          <KnowledgeCheck key={check.id} check={check} />
        ))}
      </div>
    );
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`bg-white shadow-lg transition-all duration-300 ${sidebarOpen ? 'w-80' : 'w-16'} flex flex-col`}>
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            {sidebarOpen && (
              <div>
                <h2 className="text-lg font-bold text-orange-600">FLANNERY</h2>
                <p className="text-sm text-gray-600">Training Platform</p>
              </div>
            )}
            <button 
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="p-2 hover:bg-gray-100 rounded-lg"
            >
              {sidebarOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
            </button>
          </div>

          {sidebarOpen && (
            <div className="mt-4">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search training content..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </div>
          )}
        </div>

        <nav className="flex-1 p-4 space-y-2">
          {Object.entries(trainingData).map(([key, section]) => {
            const Icon = section.icon;
            const isCompleted = completedSections.has(key);

            return (
              <button
                key={key}
                onClick={() => setActiveSection(key)}
                className={`w-full flex items-center space-x-3 px-3 py-3 rounded-lg transition-colors ${
                  activeSection === key 
                    ? 'bg-orange-100 text-orange-700 border-r-4 border-orange-500' 
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                <Icon className="h-5 w-5 flex-shrink-0" />
                {sidebarOpen && (
                  <div className="flex-1 text-left">
                    <div className="flex items-center justify-between">
                      <span className="font-medium">{section.title}</span>
                      {isCompleted && <CheckCircle className="h-4 w-4 text-green-500" />}
                    </div>
                  </div>
                )}
                {!sidebarOpen && isCompleted && (
                  <CheckCircle className="h-4 w-4 text-green-500 absolute right-1 top-1" />
                )}
              </button>
            );
          })}
        </nav>

        {sidebarOpen && (
          <div className="p-4 border-t border-gray-200">
            <div className="bg-orange-50 p-3 rounded-lg">
              <div className="flex items-center space-x-2 mb-2">
                <AlertTriangle className="h-4 w-4 text-orange-600" />
                <span className="text-sm font-semibold text-orange-700">Always Remember</span>
              </div>
              <p className="text-xs text-orange-600">OperateSAFE - Safety is everyone's responsibility</p>
            </div>
          </div>
        )}
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        <header className="bg-white shadow-sm border-b border-gray-200 p-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-xl font-semibold text-gray-900">
                {trainingData[activeSection]?.title || 'Training Dashboard'}
              </h1>
              <p className="text-sm text-gray-600">Interactive Digital Training Experience</p>
            </div>
            <div className="flex items-center space-x-4">
              <button className="p-2 hover:bg-gray-100 rounded-lg" title="Bookmark">
                <Bookmark className="h-5 w-5 text-gray-600" />
              </button>
              <button className="p-2 hover:bg-gray-100 rounded-lg" title="Reset Progress">
                <RotateCcw className="h-5 w-5 text-gray-600" />
              </button>
            </div>
          </div>
        </header>

        <main className="flex-1 overflow-auto p-6">
          <SectionContent 
            sectionKey={activeSection} 
            data={trainingData[activeSection]} 
          />
        </main>
      </div>
    </div>
  );
};

export default FlanneryTrainingApp;
